import React from 'react';
import { Formik } from 'formik';

const AddCostumer = () => {
  return (
    <>
      <h1>Cadastro de Clientes</h1>

      <Formik
        initialValues={
          { name: '', email: '@mail.com', birth: '' }
        }
        validate={(values) => {
          const errors = {};
          if (!values.name) {
            errors.name = "Campo obrigatório. Favor informar o Nome."
          }
          if (!values.email) {
            errors.email = "Campo obrigatório. Favor informar o Email."
          }
          if (!values.birth) {
            errors.birth = "Campo obrigatório. Favor informar a Data de nascimento."
          }
          return errors;
        }}
        onSubmit={(values) => {
          alert(JSON.stringify(values));
        }}
      >
        {(props) => (
          <form
            onSubmit={props.handleSubmit}
            noValidade
          >
            <div className="form-group">
              <label htmlFor="name">Nome</label>
              <input
                id="name"
                name="name"
                type="text"
                value={props.values.name}
                onChange={props.handleChange}
              />
            </div>
            {props.errors.name ? (<div>{props.errors.name}</div>) : null}
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                id="email"
                name="email"
                type="email"
                value={props.values.email}
                onChange={props.handleChange}
              />
            </div>
            {props.errors.email ? (<div>{props.errors.email}</div>) : null}

            <div className="form-group">
              <label htmlFor="date">Data de Nascimento</label>
              <input
                id="birth"
                name="birth"
                type="date"
                value={props.values.birth}
                onChange={props.handleChange}
              />
              {props.errors.birth ? (<div>{props.errors.birth}</div>) : null}

            </div>
            <button type="submit">Adicionar</button>
          </form>
        )}
      </Formik>
    </>
  );
};

export default AddCostumer;
