import React from 'react';
import { Formik } from 'formik';

const AddCostumer = () => {
  return (
    <>
      <h1>Cadastro de Clientes</h1>

      <Formik
        initialValues={
          { name: '', email: '', birth: '' }
        }
        validate={(values) => {
          const errors = {};
          if (!values.name) {
            errors.name = "Campo obrigatório. Favor informar o Nome."
          }
          if (!values.email) {
            errors.email = "Campo obrigatório. Favor informar o Email."
            
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = "O email é inválido !"
          }
          if (!values.birth) {
            errors.birth = "Campo obrigatório. Favor informar a Data de nascimento."
          }
          return errors;
        }}
        onSubmit={(values) => {
          alert(JSON.stringify(values));
        }}
      >
        {(props) => (
          <form
            onSubmit={props.handleSubmit}
            noValidade
          >
            <div className="form-group">
              <label htmlFor="name">Nome</label>
              <input
                id="name"
                name="name"
                type="text"
                value={props.values.name}
                onChange={props.handleChange}
                onBlur={props.handleBlur}
                className={props.errors.name && props.touched.name ? 'is-invalid' : ''}
              />
            </div>
            {props.errors.name && props.touched.name ?
              (<div className='invalid-feedback'>
                {props.errors.name}
              </div>)
              : null
            }
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                id="email"
                name="email"
                type="email"
                value={props.values.email}
                onChange={props.handleChange}
                onBlur={props.handleBlur}
                className={props.errors.email && props.touched.email ? 'is-invalid' : ''}
              />
            </div>
            {props.errors.email && props.touched.email ?
              (<div className='invalid-feedback' >
                {props.errors.email}
              </div>)
              : null}

            <div className="form-group">
              <label htmlFor="date">Data de Nascimento</label>
              <input
                id="birth"
                name="birth"
                type="date"
                value={props.values.birth}
                onChange={props.handleChange}
                onBlur={props.handleBlur}
              />
              {props.errors.birth && props.touched.birth ?
                (<div className='invalid-feedback'>
                  {props.errors.birth}
                </div>)
                : null}
            </div>
            <button type="submit">Adicionar</button>
          </form>
        )}
      </Formik>
    </>
  );
};

export default AddCostumer;
